import React, { useEffect, useState } from 'react';

const GifComponent = ({ cWords }) => {
  const [cGifUrl, setGifUrl] = useState('');
  const cApiKey = 'XrhlhNkC54XUvG0UDVbWFWVNJuddJvta'; // Tu clave de API

  useEffect(() => {
    const fetchGif = async () => {
      try {
        const tags = cWords.map(word => encodeURIComponent(word));
        const tagString = tags.join('+');
        const gifResponse = await fetch(`https://api.giphy.com/v1/gifs/random?api_key=${cApiKey}&tag=${tagString}`);
        const gifData = await gifResponse.json();
        const cGifUrl = gifData.data.images.downsized_large.url;
        setGifUrl(cGifUrl);
      } catch (error) {
        console.log(error);
      }
    };

    fetchGif();
  }, [cWords]);

  return (
    <div style={{textAlign: "center"}}>
      {cGifUrl ? (
        <img  style={{textAlign: "center"}} src={cGifUrl} alt="GIF" />
      ) : (
        <p style={{textAlign: "center"}}>Cargando GIF...</p>
      )}
    </div>
  );
};

export default GifComponent;
