import React, { useEffect, useState } from 'react';

const GifComponent = () => {
  const [gifUrl, setGifUrl] = useState('');
  const [words, setWords] = useState('');
  const apiKey = 'XrhlhNkC54XUvG0UDVbWFWVNJuddJvta'; // Tu clave de API

  useEffect(() => {
    const fetchGif = async () => {
      try {
        const response = await fetch('https://catfact.ninja/fact');
        const data = await response.json();
        const words = data.fact.split(' ').slice(0, 4);
        const tags = words.map(word => encodeURIComponent(word));
        const tagString = tags.join('+');
        const gifResponse = await fetch(`https://api.giphy.com/v1/gifs/random?api_key=${apiKey}&tag=${tagString}`);
        const gifData = await gifResponse.json();
        const gifUrl = gifData.data.images.downsized_large.url;
        setGifUrl(gifUrl);
      } catch (error) {
        console.log(error);
      }
    };

    fetchGif();
  }, []);

  return (
    <div>
      {gifUrl ? (
        <img src={gifUrl} alt="GIF" />
      ) : (
        <p>Cargando GIF...</p>
      )}
    </div>
  );
};

export default GifComponent;
