import React, { useEffect, useState } from 'react';

const GifComponent = () => {
  const [gifUrl, setGifUrl] = useState('');
  const apiKey = 'XrhlhNkC54XUvG0UDVbWFWVNJuddJvta'; // Tu clave de API

  useEffect(() => {
    const fetchGif = async () => {
      try {
        const tags = ['palabra1', 'palabra2', 'palabra3', 'palabra4']; // Reemplaza con tus palabras clave
        const tagString = tags.join('+');
        const response = await fetch(`https://api.giphy.com/v1/gifs/random?api_key=${apiKey}&tag=${tagString}`);
        const data = await response.json();
        const gifUrl = data.data.image_url;
        setGifUrl(gifUrl);
      } catch (error) {
        console.log(error);
      }
    };

    fetchGif();
  }, []);

  return (
    <div>
      {gifUrl ? <img src={gifUrl} alt="GIF" /> : <p>Cargando GIF...</p>}
    </div>
  );
};

export default GifComponent;
