import React, { useState, useEffect } from 'react';

function GiphyComponent() {
  const [gifUrl, setGifUrl] = useState('');

  useEffect(() => {
    const fetchGif = async () => {
      try {
        const tag = await getTagFromAPI();
        const apiKey = 'XrhlhNkC54XUvG0UDVbWFWVNJuddJvta';
        const endpoint = `https://api.giphy.com/v1/gifs/random?api_key=${apiKey}&tag=${tag}`;
        const response = await fetch(endpoint);
        const data = await response.json();
        const gif = data.data.image_original_url;
        setGifUrl(gif);
      } catch (error) {
        console.log(error);
      }
    };

    fetchGif();
  }, []);

  const getTagFromAPI = async () => {
    try {
      const response = await fetch('https://catfact.ninja/fact');
      const data = await response.json();
      const words = data.fact.split(' ').slice(0, 4).join(' ');
      return words;
    } catch (error) {
      console.log(error);
      return '';
    }
  };

  return (
    <div>
      <h1>Random GIF:</h1>
      {gifUrl ? (
        <img src={gifUrl} alt="Random GIF" />
      ) : (
        <p>Loading GIF...</p>
      )}
    </div>
  );
}

export default GiphyComponent;
